obs           = obslua; -- Rename obslua into obs
obs_settings = settings; -- Set the obs settings

obs_start_pressed = false; -- Multiple instances guard

------------------------------------------------------------------------------------------------------------------------------------------------------

ScriptName = "Smart Start";

Github = "https://github.com/jeabapLANG/obs-smart-start";
GithubScriptIcon = "data:image/svg+xml;base64,";

ScriptVersion = 1.0;

ScriptDescription = [[
	<hr/>
	<center><h2>]] .. ScriptName .. [[</h2></center>
	<center><img width=50 height=50 src="]] .. GithubScriptIcon .. [["/></center>
	<center>(Version: ]] .. ScriptVersion .. [[)</center> 

	
	<center><a href="]] .. Github .. [[">Find it on GitHub</a></center>

	<p>The <b>]] .. ScriptName .. [[</b> script automatically starts the designed applications at obs startup</p>

	<p>Select the <b>applications</b> to launch at obs startup :</p>
	<ol>
		<li><tt>Click on the + button to start adding applications</tt></li>
		<li><tt>When done click on the launch button</tt></li>
		<li><tt>Enable or disable the auto-exit button if you don't want your applications to stop when obs exit</tt></li>
	</ol>
]];

